import React, { useState } from 'react';
import Dashboard from './components/Dashboard';
import DeviceManager from './components/DeviceManager';

function App() {
  const [view, setView] = useState('dashboard');

  return (
    <div style={{ minHeight: '100vh', background: '#0f172a' }}>
      <nav style={{
        background: '#1e293b',
        padding: '1rem 2rem',
        boxShadow: '0 2px 10px rgba(0,0,0,0.3)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        borderBottom: '1px solid #334155'
      }}>
        <h1 style={{ margin: 0, fontSize: '1.5rem', color: '#f1f5f9', fontWeight: '600' }}>‚ö° Modbus Gateway</h1>
        <div style={{ display: 'flex', gap: '0.5rem' }}>
          <button onClick={() => setView('dashboard')} style={{
            padding: '0.6rem 1.5rem',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
            fontSize: '0.95rem',
            fontWeight: '500',
            transition: 'all 0.3s',
            background: view === 'dashboard' ? 'linear-gradient(135deg, #3b82f6 0%, #8b5cf6 100%)' : '#334155',
            color: 'white'
          }}>üìä Dashboard</button>
          <button onClick={() => setView('manager')} style={{
            padding: '0.6rem 1.5rem',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
            fontSize: '0.95rem',
            fontWeight: '500',
            transition: 'all 0.3s',
            background: view === 'manager' ? 'linear-gradient(135deg, #3b82f6 0%, #8b5cf6 100%)' : '#334155',
            color: 'white'
          }}>‚öôÔ∏è Devices</button>
        </div>
      </nav>
      <div style={{ padding: '2rem' }}>
        {view === 'dashboard' ? <Dashboard /> : <DeviceManager />}
      </div>
    </div>
  );
}

export default App;
