import React, { useState, useEffect } from 'react';
import {
  Grid,
  Card,
  CardContent,
  Typography,
  Box,
  Chip,
  LinearProgress,
  Paper,
  IconButton,
  Tooltip
} from '@mui/material';
import {
  Speed,
  ElectricBolt,
  Scale,
  CheckCircle,
  Error,
  Refresh,
  TrendingUp,
  Factory
} from '@mui/icons-material';
import { styled } from '@mui/material/styles';

const StyledCard = styled(Card)(({ theme }) => ({
  borderRadius: 16,
  background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
  color: 'white',
  position: 'relative',
  overflow: 'visible',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 100%)',
    borderRadius: 16,
  }
}));

const MetricCard = styled(Card)(({ theme }) => ({
  borderRadius: 12,
  background: theme.palette.background.paper,
  boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
  transition: 'transform 0.2s ease-in-out',
  '&:hover': {
    transform: 'translateY(-4px)',
  }
}));

const StatusChip = styled(Chip)(({ status }) => ({
  fontWeight: 600,
  ...(status === 'online' && {
    backgroundColor: '#4caf50',
    color: 'white',
  }),
  ...(status === 'offline' && {
    backgroundColor: '#f44336',
    color: 'white',
  })
}));

function Dashboard() {
  const [data, setData] = useState({});
  const [devices, setDevices] = useState([]);
  const [lastUpdate, setLastUpdate] = useState(new Date());

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [dataRes, devicesRes] = await Promise.all([
          fetch('/api/data'),
          fetch('/api/devices')
        ]);

        if (dataRes.ok && devicesRes.ok) {
          setData(await dataRes.json());
          setDevices(await devicesRes.json());
          setLastUpdate(new Date());
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 2000);
    return () => clearInterval(interval);
  }, []);

  const getDeviceIcon = (type) => {
    switch (type) {
      case 'oee': return <Factory />;
      case 'pm': return <ElectricBolt />;
      case 'scale': return <Scale />;
      default: return <Speed />;
    }
  };

  const getDeviceColor = (type) => {
    switch (type) {
      case 'oee': return '#2196f3';
      case 'pm': return '#ff9800';
      case 'scale': return '#4caf50';
      default: return '#9c27b0';
    }
  };

  const onlineDevices = Object.values(data).filter(d => d.status === 'online').length;
  const totalDevices = devices.length;

  return (
    <Box>
      {/* Header */}
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Box>
          <Typography variant="h4" fontWeight="bold" gutterBottom>
            System Dashboard
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Real-time monitoring of industrial devices
          </Typography>
        </Box>
        <Box display="flex" alignItems="center" gap={2}>
          <Typography variant="body2" color="text.secondary">
            Last update: {lastUpdate.toLocaleTimeString()}
          </Typography>
          <Tooltip title="Refresh">
            <IconButton onClick={() => window.location.reload()}>
              <Refresh />
            </IconButton>
          </Tooltip>
        </Box>
      </Box>

      {/* Summary Cards */}
      <Grid container spacing={3} mb={4}>
        <Grid item xs={12} sm={6} md={3}>
          <StyledCard>
            <CardContent>
              <Box display="flex" alignItems="center" justifyContent="space-between">
                <Box>
                  <Typography variant="h3" fontWeight="bold">
                    {totalDevices}
                  </Typography>
                  <Typography variant="body1" sx={{ opacity: 0.9 }}>
                    Total Devices
                  </Typography>
                </Box>
                <Factory sx={{ fontSize: 48, opacity: 0.7 }} />
              </Box>
            </CardContent>
          </StyledCard>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <StyledCard>
            <CardContent>
              <Box display="flex" alignItems="center" justifyContent="space-between">
                <Box>
                  <Typography variant="h3" fontWeight="bold">
                    {onlineDevices}
                  </Typography>
                  <Typography variant="body1" sx={{ opacity: 0.9 }}>
                    Online
                  </Typography>
                </Box>
                <CheckCircle sx={{ fontSize: 48, opacity: 0.7 }} />
              </Box>
            </CardContent>
          </StyledCard>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <StyledCard>
            <CardContent>
              <Box display="flex" alignItems="center" justifyContent="space-between">
                <Box>
                  <Typography variant="h3" fontWeight="bold">
                    {totalDevices - onlineDevices}
                  </Typography>
                  <Typography variant="body1" sx={{ opacity: 0.9 }}>
                    Offline
                  </Typography>
                </Box>
                <Error sx={{ fontSize: 48, opacity: 0.7 }} />
              </Box>
            </CardContent>
          </StyledCard>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <StyledCard>
            <CardContent>
              <Box display="flex" alignItems="center" justifyContent="space-between">
                <Box>
                  <Typography variant="h3" fontWeight="bold">
                    {totalDevices > 0 ? Math.round((onlineDevices / totalDevices) * 100) : 0}%
                  </Typography>
                  <Typography variant="body1" sx={{ opacity: 0.9 }}>
                    Uptime
                  </Typography>
                </Box>
                <TrendingUp sx={{ fontSize: 48, opacity: 0.7 }} />
              </Box>
              <LinearProgress 
                variant="determinate" 
                value={totalDevices > 0 ? (onlineDevices / totalDevices) * 100 : 0}
                sx={{ mt: 1, bgcolor: 'rgba(255,255,255,0.3)', '& .MuiLinearProgress-bar': { bgcolor: 'white' } }}
              />
            </CardContent>
          </StyledCard>
        </Grid>
      </Grid>

      {/* Device Cards */}
      <Typography variant="h5" fontWeight="bold" mb={3}>
        Device Status
      </Typography>

      <Grid container spacing={3}>
        {devices.map((device) => {
          const deviceData = data[device.name] || {};
          const isOnline = deviceData.status === 'online';

          return (
            <Grid item xs={12} sm={6} md={4} key={device.name}>
              <MetricCard>
                <CardContent>
                  <Box display="flex" alignItems="center" justifyContent="space-between" mb={2}>
                    <Box display="flex" alignItems="center">
                      <Box 
                        sx={{ 
                          p: 1, 
                          borderRadius: 2, 
                          bgcolor: getDeviceColor(device.type) + '20',
                          color: getDeviceColor(device.type),
                          mr: 2
                        }}
                      >
                        {getDeviceIcon(device.type)}
                      </Box>
                      <Box>
                        <Typography variant="h6" fontWeight="bold">
                          {device.name}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          {device.type.toUpperCase()} â€¢ {device.ip}:{device.port}
                        </Typography>
                      </Box>
                    </Box>
                    <StatusChip 
                      label={isOnline ? 'Online' : 'Offline'} 
                      status={isOnline ? 'online' : 'offline'}
                      size="small"
                    />
                  </Box>

                  {isOnline && deviceData.values && (
                    <Box>
                      <Typography variant="body2" color="text.secondary" mb={1}>
                        Live Data:
                      </Typography>
                      <Box display="flex" flexWrap="wrap" gap={1}>
                        {Object.entries(deviceData.values).slice(0, 3).map(([key, value]) => (
                          <Chip 
                            key={key}
                            label={`${key}: ${typeof value === 'number' ? value.toFixed(1) : value}`}
                            size="small"
                            variant="outlined"
                          />
                        ))}
                      </Box>
                    </Box>
                  )}

                  {!isOnline && deviceData.error && (
                    <Typography variant="body2" color="error" sx={{ mt: 1 }}>
                      Error: {deviceData.error}
                    </Typography>
                  )}
                </CardContent>
              </MetricCard>
            </Grid>
          );
        })}
      </Grid>

      {devices.length === 0 && (
        <Paper sx={{ p: 4, textAlign: 'center', mt: 4 }}>
          <Factory sx={{ fontSize: 64, color: 'text.secondary', mb: 2 }} />
          <Typography variant="h6" color="text.secondary">
            No devices configured
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Go to the Devices page to add your first device
          </Typography>
        </Paper>
      )}
    </Box>
  );
}

export default Dashboard;
