import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PMParamsEditor from './PMParamsEditor';

const API = `http://${window.location.hostname}:8000/api`;

function DeviceManager() {
  const [devices, setDevices] = useState([]);
  const [form, setForm] = useState({ name: '', ip: '', port: 502, slave_id: 1, type: 'oee', offset: 0, pm_params: null });
  const [editing, setEditing] = useState(null);
  const [showPMEditor, setShowPMEditor] = useState(false);

  useEffect(() => { loadDevices(); }, []);

  const loadDevices = async () => {
    const res = await axios.get(`${API}/devices`);
    setDevices(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editing) {
      await axios.put(`${API}/devices/${editing}`, form);
      setEditing(null);
    } else {
      await axios.post(`${API}/devices`, form);
    }
    setForm({ name: '', ip: '', port: 502, slave_id: 1, type: 'oee', offset: 0, pm_params: null });
    loadDevices();
  };

  const handleDelete = async (name) => {
    if (window.confirm(`Delete ${name}?`)) {
      await axios.delete(`${API}/devices/${name}`);
      loadDevices();
    }
  };

  const handleEdit = (d) => {
    setForm(d);
    setEditing(d.name);
  };

  const handlePMParamsSave = (params) => {
    setForm({...form, pm_params: params});
    setShowPMEditor(false);
  };

  const input = { padding: '0.7rem', border: '2px solid #334155', borderRadius: '8px', fontSize: '0.95rem', outline: 'none', background: '#1e293b', color: '#f1f5f9' };
  const btn = { padding: '0.7rem 1.5rem', border: 'none', borderRadius: '8px', cursor: 'pointer', fontSize: '0.95rem', fontWeight: '500', transition: 'all 0.3s' };

  return (
    <div>
      <h2 style={{ margin: '0 0 1.5rem 0', color: '#f1f5f9', fontSize: '1.8rem', fontWeight: '600' }}>Device Manager</h2>

      <form onSubmit={handleSubmit} style={{
        background: '#1e293b',
        padding: '2rem',
        borderRadius: '12px',
        boxShadow: '0 4px 6px rgba(0,0,0,0.3)',
        marginBottom: '2rem',
        border: '1px solid #334155'
      }}>
        <h3 style={{ margin: '0 0 1rem 0', color: '#f1f5f9', fontSize: '1.2rem' }}>{editing ? '‚úèÔ∏è Edit Device' : '‚ûï Add Device'}</h3>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem' }}>
          <input style={input} placeholder="Name" value={form.name} onChange={e => setForm({...form, name: e.target.value})} required />
          <input style={input} placeholder="IP" value={form.ip} onChange={e => setForm({...form, ip: e.target.value})} required />
          <input style={input} type="number" placeholder="Port" value={form.port} onChange={e => setForm({...form, port: parseInt(e.target.value)})} required />
          <input style={input} type="number" placeholder="Slave ID" value={form.slave_id} onChange={e => setForm({...form, slave_id: parseInt(e.target.value)})} required />
          <select style={input} value={form.type} onChange={e => setForm({...form, type: e.target.value})}>
            <option value="oee">OEE</option>
            <option value="pm">Power Meter</option>
            <option value="scale">Scale</option>
          </select>
          <div style={{...input, backgroundColor: '#0f172a', display: 'flex', alignItems: 'center', color: '#94a3b8'}}>Offset: Auto</div>
        </div>
        {form.type === 'pm' && (
          <button type="button" onClick={() => setShowPMEditor(true)} style={{...btn, marginTop: '1rem', background: '#f59e0b', color: 'white'}}>
            ‚öôÔ∏è {form.pm_params ? 'Edit PM Parameters (Custom)' : 'Configure PM Parameters (Default)'}
          </button>
        )}
        <div style={{marginTop: '1rem', display: 'flex', gap: '0.5rem'}}>
          <button type="submit" style={{...btn, background: 'linear-gradient(135deg, #3b82f6 0%, #8b5cf6 100%)', color: 'white'}}>
            {editing ? 'üíæ Update' : '‚ûï Add'}
          </button>
          {editing && <button type="button" onClick={() => { setEditing(null); setForm({ name: '', ip: '', port: 502, slave_id: 1, type: 'oee', offset: 0, pm_params: null }); }} style={{...btn, background: '#475569', color: '#f1f5f9'}}>‚úñ Cancel</button>}
        </div>
      </form>

      {showPMEditor && (
        <PMParamsEditor 
          deviceName={form.name || 'New Device'}
          currentParams={form.pm_params}
          onSave={handlePMParamsSave}
          onCancel={() => setShowPMEditor(false)}
        />
      )}

      <div style={{
        background: '#1e293b',
        borderRadius: '12px',
        boxShadow: '0 4px 6px rgba(0,0,0,0.3)',
        overflow: 'hidden',
        border: '1px solid #334155'
      }}>
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ background: 'linear-gradient(135deg, #3b82f6 0%, #8b5cf6 100%)', color: 'white' }}>
              {['Name', 'IP', 'Port', 'Slave', 'Type', 'Offset', 'PM Config', 'Actions'].map(h => 
                <th key={h} style={{ padding: '1rem', textAlign: 'left', fontWeight: '600' }}>{h}</th>
              )}
            </tr>
          </thead>
          <tbody>
            {devices.map((d, i) => (
              <tr key={d.name} style={{ background: i % 2 === 0 ? '#0f172a' : '#1e293b', borderBottom: '1px solid #334155' }}>
                <td style={{ padding: '1rem', fontWeight: '500', color: '#f1f5f9' }}>{d.name}</td>
                <td style={{ padding: '1rem', color: '#cbd5e1' }}>{d.ip}</td>
                <td style={{ padding: '1rem', color: '#cbd5e1' }}>{d.port}</td>
                <td style={{ padding: '1rem', color: '#cbd5e1' }}>{d.slave_id}</td>
                <td style={{ padding: '1rem' }}>
                  <span style={{
                    padding: '0.3rem 0.8rem',
                    borderRadius: '20px',
                    fontSize: '0.75rem',
                    fontWeight: '600',
                    background: d.type === 'oee' ? '#3b82f6' : d.type === 'pm' ? '#f59e0b' : '#10b981',
                    color: 'white',
                    textTransform: 'uppercase'
                  }}>{d.type}</span>
                </td>
                <td style={{ padding: '1rem', color: '#cbd5e1' }}>{d.offset}</td>
                <td style={{ padding: '1rem' }}>
                  {d.type === 'pm' ? (
                    <span style={{
                      padding: '0.3rem 0.8rem',
                      borderRadius: '20px',
                      fontSize: '0.75rem',
                      fontWeight: '500',
                      background: d.pm_params ? '#fbbf24' : '#3b82f6',
                      color: 'white'
                    }}>{d.pm_params ? '‚öôÔ∏è Custom' : 'üìã Default'}</span>
                  ) : <span style={{ color: '#64748b' }}>-</span>}
                </td>
                <td style={{ padding: '1rem' }}>
                  <div style={{ display: 'flex', gap: '0.5rem' }}>
                    <button onClick={() => handleEdit(d)} style={{ ...btn, padding: '0.5rem 1rem', background: '#3b82f6', color: 'white' }}>‚úèÔ∏è</button>
                    <button onClick={() => handleDelete(d.name)} style={{ ...btn, padding: '0.5rem 1rem', background: '#ef4444', color: 'white' }}>üóëÔ∏è</button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default DeviceManager;
