import React, { useState } from 'react';
import {
  Box,
  Card,
  CardContent,
  TextField,
  Button,
  Typography,
  Alert,
  Container,
  InputAdornment,
  IconButton,
  Paper,
  Divider,
  Fade
} from '@mui/material';
import {
  Visibility,
  VisibilityOff,
  Lock,
  Person,
  Security,
  LockOpen
} from '@mui/icons-material';
import { styled, keyframes } from '@mui/material/styles';

const float = keyframes`
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
`;

const LoginContainer = styled(Container)(({ theme }) => ({
  minHeight: '100vh',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  background: 'linear-gradient(135deg, #0a0e27 0%, #1a1d3a 50%, #2a2d5a 100%)',
  position: 'relative',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'radial-gradient(circle at 20% 80%, rgba(33, 150, 243, 0.1) 0%, transparent 50%), radial-gradient(circle at 80% 20%, rgba(245, 0, 87, 0.1) 0%, transparent 50%)',
  }
}));

const LoginCard = styled(Card)(({ theme }) => ({
  maxWidth: 450,
  width: '100%',
  borderRadius: 20,
  background: 'rgba(26, 29, 58, 0.95)',
  backdropFilter: 'blur(20px)',
  border: '1px solid rgba(255, 255, 255, 0.1)',
  boxShadow: '0 25px 50px rgba(0, 0, 0, 0.5), 0 0 0 1px rgba(255, 255, 255, 0.05)',
  position: 'relative',
  zIndex: 1,
}));

const LogoContainer = styled(Box)(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  marginBottom: theme.spacing(4),
  animation: `${float} 3s ease-in-out infinite`,
}));

const StyledTextField = styled(TextField)(({ theme }) => ({
  '& .MuiOutlinedInput-root': {
    borderRadius: 12,
    backgroundColor: 'rgba(255, 255, 255, 0.06)',
    border: '1px solid rgba(255, 255, 255, 0.15)',
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: 'rgba(255, 255, 255, 0.1)',
      borderColor: 'rgba(33, 150, 243, 0.4)',
    },
    '&.Mui-focused': {
      backgroundColor: 'rgba(255, 255, 255, 0.12)',
      borderColor: '#2196f3',
      boxShadow: '0 0 0 2px rgba(33, 150, 243, 0.2)',
    },
    '& fieldset': {
      border: 'none',
    },
  },
  '& .MuiInputLabel-root': {
    color: 'rgba(255, 255, 255, 0.7)',
    '&.Mui-focused': {
      color: '#2196f3',
    },
  },
  '& .MuiOutlinedInput-input': {
    color: 'white',
    padding: '14px 16px',
  },
}));

const LoginButton = styled(Button)(({ theme }) => ({
  borderRadius: 12,
  padding: theme.spacing(1.5, 0),
  fontSize: '1.1rem',
  fontWeight: 600,
  background: 'linear-gradient(135deg, #2196f3 0%, #1976d2 100%)',
  boxShadow: '0 8px 25px rgba(33, 150, 243, 0.3)',
  transition: 'all 0.3s ease',
  '&:hover': {
    background: 'linear-gradient(135deg, #1976d2 0%, #1565c0 100%)',
    boxShadow: '0 12px 35px rgba(33, 150, 243, 0.4)',
    transform: 'translateY(-2px)',
  },
}));

const CredentialsBox = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  backgroundColor: 'rgba(255, 255, 255, 0.05)',
  borderRadius: 12,
  border: '1px solid rgba(255, 255, 255, 0.1)',
  textAlign: 'center',
}));

function Login({ onLogin }) {
  const [credentials, setCredentials] = useState({ username: '', password: '' });
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    // Simulate loading for better UX
    await new Promise(resolve => setTimeout(resolve, 800));

    if (credentials.username === 'admin' && credentials.password === 'admin') {
      onLogin();
    } else {
      setError('Invalid username or password');
    }
    setLoading(false);
  };

  return (
    <LoginContainer maxWidth={false}>
      <Fade in timeout={800}>
        <LoginCard>
          <CardContent sx={{ p: 4 }}>
            <LogoContainer>
              <Box 
                sx={{ 
                  p: 2, 
                  borderRadius: '50%', 
                  background: 'linear-gradient(135deg, #2196f3 0%, #1976d2 100%)',
                  mr: 2,
                  boxShadow: '0 8px 25px rgba(33, 150, 243, 0.3)'
                }}
              >
                <Security sx={{ fontSize: 32, color: 'white' }} />
              </Box>
              <Box>
                <Typography variant="h4" fontWeight="bold" color="white">
                  Modbus Gateway
                </Typography>
                <Typography variant="body2" color="rgba(255, 255, 255, 0.7)">
                  Industrial Control System
                </Typography>
              </Box>
            </LogoContainer>

            <Typography 
              variant="h6" 
              align="center" 
              color="rgba(255, 255, 255, 0.8)" 
              mb={4}
              fontWeight={300}
            >
              Secure Access Portal
            </Typography>

            {error && (
              <Alert 
                severity="error" 
                sx={{ 
                  mb: 3, 
                  borderRadius: 2,
                  backgroundColor: 'rgba(244, 67, 54, 0.1)',
                  border: '1px solid rgba(244, 67, 54, 0.3)',
                  color: '#ff6b6b'
                }}
              >
                {error}
              </Alert>
            )}

            <Box component="form" onSubmit={handleSubmit}>
              <StyledTextField
                fullWidth
                label="Username"
                variant="outlined"
                margin="normal"
                value={credentials.username}
                onChange={(e) => setCredentials({ ...credentials, username: e.target.value })}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <Person sx={{ color: 'rgba(255, 255, 255, 0.5)' }} />
                    </InputAdornment>
                  )
                }}
                required
              />

              <StyledTextField
                fullWidth
                label="Password"
                type={showPassword ? 'text' : 'password'}
                variant="outlined"
                margin="normal"
                value={credentials.password}
                onChange={(e) => setCredentials({ ...credentials, password: e.target.value })}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <Lock sx={{ color: 'rgba(255, 255, 255, 0.5)' }} />
                    </InputAdornment>
                  ),
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton 
                        onClick={() => setShowPassword(!showPassword)}
                        sx={{ color: 'rgba(255, 255, 255, 0.5)' }}
                      >
                        {showPassword ? <VisibilityOff /> : <Visibility />}
                      </IconButton>
                    </InputAdornment>
                  )
                }}
                required
              />

              <LoginButton
                type="submit"
                fullWidth
                variant="contained"
                size="large"
                disabled={loading}
                sx={{ mt: 4, mb: 3 }}
                startIcon={loading ? null : <LockOpen />}
              >
                {loading ? 'Authenticating...' : 'Sign In'}
              </LoginButton>
            </Box>

            <Divider sx={{ my: 3, borderColor: 'rgba(255, 255, 255, 0.1)' }} />

            <CredentialsBox>
              <Typography 
                variant="caption" 
                color="rgba(255, 255, 255, 0.6)" 
                display="block"
                mb={1}
              >
                Development Credentials
              </Typography>
              <Typography variant="body2" color="rgba(255, 255, 255, 0.8)">
                <strong>Username:</strong> admin â€¢ <strong>Password:</strong> admin
              </Typography>
            </CredentialsBox>
          </CardContent>
        </LoginCard>
      </Fade>
    </LoginContainer>
  );
}

export default Login;
