import React from 'react';

function DeviceCard({ name, data }) {
  const online = data.status === 'online';

  return (
    <div style={{ background: '#1e293b', borderRadius: '8px', padding: '1rem', border: `2px solid ${online ? '#10b981' : '#475569'}` }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
        <h3 style={{ margin: 0, fontSize: '1rem' }}>{name}</h3>
        <div style={{ width: '10px', height: '10px', borderRadius: '50%', background: online ? '#10b981' : '#64748b' }} />
      </div>

      {data.values && (
        <div style={{ background: '#0f172a', padding: '0.5rem', borderRadius: '6px', fontSize: '0.8rem' }}>
          {Object.entries(data.values).map(([key, value]) => (
            <div key={key} style={{ display: 'flex', justifyContent: 'space-between', padding: '0.2rem 0' }}>
              <span style={{ color: '#94a3b8' }}>{key}:</span>
              <span style={{ color: '#f1f5f9' }}>{typeof value === 'number' ? value.toFixed(2) : value}</span>
            </div>
          ))}
        </div>
      )}

      {data.error && <div style={{ color: '#f87171', fontSize: '0.75rem', marginTop: '0.5rem' }}>⚠️ {data.error}</div>}
      <div style={{ fontSize: '0.7rem', color: '#64748b', marginTop: '0.5rem' }}>{new Date(data.timestamp * 1000).toLocaleTimeString()}</div>
    </div>
  );
}

export default DeviceCard;
