import React from 'react';

function DeviceCard({ name, data }) {
  const online = data.status === 'online';

  return (
    <div style={{
      background: 'rgba(255,255,255,0.95)',
      borderRadius: '12px',
      padding: '1.5rem',
      boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
      border: `2px solid ${online ? '#48bb78' : '#cbd5e0'}`,
      transition: 'transform 0.2s'
    }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
        <h3 style={{ margin: 0, fontSize: '1.2rem', color: '#2d3748', fontWeight: '600' }}>{name}</h3>
        <div style={{
          width: '12px',
          height: '12px',
          borderRadius: '50%',
          background: online ? '#48bb78' : '#cbd5e0',
          boxShadow: online ? '0 0 8px #48bb78' : 'none'
        }} />
      </div>

      {data.values && (
        <div style={{
          background: '#f7fafc',
          padding: '1rem',
          borderRadius: '8px',
          fontSize: '0.85rem'
        }}>
          {Object.entries(data.values).map(([key, value]) => (
            <div key={key} style={{ display: 'flex', justifyContent: 'space-between', padding: '0.3rem 0' }}>
              <span style={{ color: '#4a5568', fontWeight: '500' }}>{key.toUpperCase()}:</span>
              <strong style={{ color: '#2d3748' }}>{typeof value === 'number' ? value.toFixed(2) : value}</strong>
            </div>
          ))}
        </div>
      )}

      {data.error && <div style={{ color: '#f56565', fontSize: '0.8rem', marginTop: '0.8rem', padding: '0.5rem', background: '#fff5f5', borderRadius: '6px' }}>‚ö†Ô∏è {data.error}</div>}

      <div style={{ fontSize: '0.75rem', color: '#a0aec0', marginTop: '0.8rem' }}>
        üïí {new Date(data.timestamp * 1000).toLocaleTimeString()}
      </div>
    </div>
  );
}

export default DeviceCard;
