import React, { useState, useEffect } from 'react';
import DeviceCard from './DeviceCard';

function Dashboard() {
  const [data, setData] = useState({});
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:8000/api/ws');
    ws.onopen = () => setConnected(true);
    ws.onclose = () => setConnected(false);
    ws.onmessage = (e) => setData(JSON.parse(e.data));
    return () => ws.close();
  }, []);

  const devices = Object.entries(data);
  const online = devices.filter(([_, d]) => d.status === 'online').length;
  const offline = devices.length - online;

  return (
    <div>
      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1.5rem' }}>
        <h2 style={{ margin: 0, color: 'white', fontSize: '1.8rem', fontWeight: '600' }}>Live Dashboard</h2>
        <span style={{
          padding: '0.4rem 0.8rem',
          borderRadius: '20px',
          fontSize: '0.85rem',
          fontWeight: '500',
          background: connected ? '#48bb78' : '#f56565',
          color: 'white'
        }}>{connected ? '● Connected' : '● Disconnected'}</span>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem', marginBottom: '2rem' }}>
        <div style={{
          background: 'rgba(255,255,255,0.95)',
          padding: '1.5rem',
          borderRadius: '12px',
          boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
        }}>
          <div style={{ fontSize: '0.9rem', color: '#718096', marginBottom: '0.5rem' }}>Total Devices</div>
          <div style={{ fontSize: '2rem', fontWeight: '700', color: '#2d3748' }}>{devices.length}</div>
        </div>
        <div style={{
          background: 'rgba(255,255,255,0.95)',
          padding: '1.5rem',
          borderRadius: '12px',
          boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
        }}>
          <div style={{ fontSize: '0.9rem', color: '#718096', marginBottom: '0.5rem' }}>Online</div>
          <div style={{ fontSize: '2rem', fontWeight: '700', color: '#48bb78' }}>{online}</div>
        </div>
        <div style={{
          background: 'rgba(255,255,255,0.95)',
          padding: '1.5rem',
          borderRadius: '12px',
          boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
        }}>
          <div style={{ fontSize: '0.9rem', color: '#718096', marginBottom: '0.5rem' }}>Offline</div>
          <div style={{ fontSize: '2rem', fontWeight: '700', color: '#f56565' }}>{offline}</div>
        </div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(320px, 1fr))', gap: '1.5rem' }}>
        {devices.map(([name, d]) => <DeviceCard key={name} name={name} data={d} />)}
      </div>
      {devices.length === 0 && <p style={{ color: 'white', textAlign: 'center', fontSize: '1.1rem' }}>No devices configured. Add devices in the Devices tab.</p>}
    </div>
  );
}

export default Dashboard;
